// test-embed-http.ts
import 'dotenv/config';
import fetch from 'node-fetch';

const KEY = process.env.GOOGLE_API_KEY;
if (!KEY) throw new Error('Missing GOOGLE_API_KEY in environment');

const BASE = 'https://generativelanguage.googleapis.com/v1beta2';

// Helper: exit on HTTP errors
async function check(res: Response) {
    if (!res.ok) {
        const body = await res.text();
        console.error(`Γ¥î HTTP ${res.status}:`, body);
        process.exit(1);
    }
    return res.json();
}

async function main() {
    console.log('≡ƒöì Listing available modelsΓÇª');
    const list = await fetch(`${BASE}/models?key=${KEY}`);
    const listJson: any = await check(list);
    console.log('Γ£à Models available:',
        (listJson.models as any[]).map(m => m.name).join(', ')
    );

    console.log('\n≡ƒöì Testing embedding with embedding-gecko-001ΓÇª');
    const embed = await fetch(
        `${BASE}/models/embedding-gecko-001:embed?key=${KEY}`,
        {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({
                instances: [
                    'What does the Bible say about forgiveness?'
                ]
            }),
        }
    );
    const embedJson: any = await check(embed);

    const dims = embedJson.embeddings?.[0]?.values;
    if (!Array.isArray(dims)) {
        console.error('Γ¥î Unexpected embed response:', JSON.stringify(embedJson, null, 2));
        process.exit(1);
    }

    console.log('Γ£à Embedding OK. First 5 dims:', dims.slice(0, 5));
}

main().catch(err => {
    console.error('≡ƒöÑ Fatal error:', err);
    process.exit(1);
});

'use server';
/**
 * @fileOverview Answers theological questions based on a provided library of Bible interpretations.
 *
 * - answerTheologicalQuestion - A function that answers theological questions.
 */

import { ai } from '@/ai/genkit';
import { z } from 'genkit';
import { supabase } from '@/lib/supabase';
import { AnswerTheologicalQuestionInputSchema, AnswerTheologicalQuestionOutputSchema, type AnswerTheologicalQuestionInput, type AnswerTheologicalQuestionOutput } from '@/ai/schemas';

export async function answerTheologicalQuestion(input: AnswerTheologicalQuestionInput): Promise<AnswerTheologicalQuestionOutput> {
  return answerTheologicalQuestionFlow(input);
}

const PromptInputSchema = z.object({
  question: z.string().describe('The theological question to answer.'),
  documents: z.array(z.object({
    content: z.string(),
    fileName: z.string(),
  })).describe('A library of documents to search for answers.'),
});

const answerTheologicalQuestionPrompt = ai.definePrompt({
  name: 'answerTheologicalQuestionPrompt',
  input: { schema: PromptInputSchema },
  output: { schema: AnswerTheologicalQuestionOutputSchema },
  prompt: `You are a friendly and wise theological expert. Your goal is to help users understand complex topics by providing clear, elaborate, and conversational answers.

Engage the user in a thoughtful way. Your answer should be comprehensive and well-structured. Start with a friendly opening. Use paragraphs to explain concepts and feel free to use bullet points or lists if it helps clarify information.

Base your answer ONLY on the content of the provided documents. Do not use any other information.

If you cannot answer the question based on the documents, politely explain that the provided library does not contain the information needed to answer the question fully.

Your final output must be a JSON object that includes two keys: "answer" (your full, conversational response) and "sourceFiles" (an array of the file names from the provided documents that you used).

User's Question: {{{question}}}

Relevant Documents:
{{#each documents}}
---
File: {{{fileName}}}
Content:
{{{content}}}
---
{{/each}}

Now, please provide your thoughtful answer in the specified JSON format.`,
});

const answerTheologicalQuestionFlow = ai.defineFlow(
  {
    name: 'answerTheologicalQuestionFlow',
    inputSchema: AnswerTheologicalQuestionInputSchema,
    outputSchema: AnswerTheologicalQuestionOutputSchema,
  },
  async (input) => {
    if (!input || typeof input !== 'object' || input === null) {
      throw new Error('Invalid input to flow: input must be an object.');
    }

    const { embedding } = await ai.embed({
      model: 'googleai/text-embedding-004',
      content: input.question,
    });

    const { data: documents, error } = await supabase.rpc('match_documents', {
      query_embedding: embedding,
      match_count: 10,
      match_threshold: 0.7,
    });

    if (error) {
      console.error('Supabase error:', error);
      throw new Error(`Database error: ${error.message}`);
    }

    if (!Array.isArray(documents) || documents.length === 0) {
      return {
        answer: "I couldn't find any relevant information in my knowledge base to answer your question. Please try rephrasing it.",
        sourceFiles: [],
      };
    }

    const relevantDocuments = documents.map((doc: any) => ({
      content: doc.content,
      fileName: doc.file_name,
    }));

    const augmentedInput = { ...input, documents: relevantDocuments };
    const response = await answerTheologicalQuestionPrompt(augmentedInput);
    const output = response?.output;

    if (!output || typeof output !== 'object' || output === null) {
      return {
        answer: "I'm sorry, I had trouble generating a complete response. The AI model might be overloaded. Could you please try asking again?",
        sourceFiles: [],
      };
    }

    const finalOutput = {
      answer: output.answer || 'The AI returned a response without an answer text.',
      sourceFiles: Array.isArray(output.sourceFiles) ? output.sourceFiles : [],
    };

    if (finalOutput.sourceFiles.length > 0) {
      const retrievedFileNames = relevantDocuments.map(d => d.fileName);
      finalOutput.sourceFiles = finalOutput.sourceFiles.filter(fileName => retrievedFileNames.includes(fileName));
    }

    return finalOutput;
  }
);

'use server';

/**
 * @fileOverview Provides interpretations of a specific Bible verse based on the interpretation library.
 *
 * - interpretVerse - A function that handles the verse interpretation process.
 */

import {ai} from '@/ai/genkit';
import {z} from 'genkit';
import { supabase } from '@/lib/supabase';
import { InterpretVerseInputSchema, InterpretVerseOutputSchema, type InterpretVerseInput, type InterpretVerseOutput } from '@/ai/schemas';


export async function interpretVerse(input: InterpretVerseInput): Promise<InterpretVerseOutput> {
  return interpretVerseFlow(input);
}

const PromptInputSchema = InterpretVerseInputSchema.extend({
    documents: z.array(z.object({
        content: z.string(),
        fileName: z.string(),
    })).describe('A library of documents to search for interpretations.'),
});

const prompt = ai.definePrompt({
  name: 'interpretVersePrompt',
  input: {schema: PromptInputSchema},
  output: {schema: InterpretVerseOutputSchema},
  prompt: `You are a friendly and knowledgeable Bible scholar. Your goal is to help users understand Bible verses by providing clear, elaborate, and conversational interpretations based on a curated library.

Engage the user in a thoughtful way. Start with a friendly opening. Your interpretation should be comprehensive and well-structured, using only the information available in the provided library documents.

If the library doesn't contain relevant information, politely explain that you cannot provide an interpretation based on the available materials.

Your final output must be a JSON object that includes two keys: "interpretation" (your full, conversational response) and "sourceFiles" (an array of the file names from the provided documents that you used).

Verse Reference: {{{verseReference}}}
User Question: {{{userQuestion}}}

Relevant Interpretation Library:
{{#each documents}}
---
File: {{{fileName}}}
Content:
{{{content}}}
---
{{/each}}

Now, please provide your thoughtful interpretation in the specified JSON format.
  `,
});

const interpretVerseFlow = ai.defineFlow(
  {
    name: 'interpretVerseFlow',
    inputSchema: InterpretVerseInputSchema,
    outputSchema: InterpretVerseOutputSchema,
  },
  async input => {
    if (!input || typeof input !== 'object' || input === null) {
      throw new Error('Invalid input to flow: input must be an object.');
    }

    const query = `Interpretation for ${input.verseReference}: ${input.userQuestion}`;

    const { embedding } = await ai.embed({
        model: 'googleai/text-embedding-004',
        content: query,
    });

    const { data: documents, error } = await supabase.rpc('match_documents', {
        query_embedding: embedding,
        match_count: 10,
        match_threshold: 0.7,
    });

    if (error) {
        console.error('Supabase error:', error);
        throw new Error(`Database error: ${error.message}`);
    }

    if (!Array.isArray(documents) || documents.length === 0) {
        return {
          interpretation: "I couldn't find any relevant information in my knowledge base to answer this. Please try rephrasing your question.",
          sourceFiles: [],
        };
    }

    const relevantDocuments = documents.map((doc: any) => ({
        content: doc.content,
        fileName: doc.file_name,
    }));

    const augmentedInput = {...input, documents: relevantDocuments};
    const response = await prompt(augmentedInput);
    const output = response?.output;

    if (!output || typeof output !== 'object' || output === null) {
      return {
        interpretation: "I'm sorry, I had trouble generating a complete interpretation. The AI model might be overloaded. Could you please try asking again?",
        sourceFiles: [],
      };
    }

    const finalOutput = {
      interpretation: output.interpretation || 'The AI returned a response without an interpretation text.',
      sourceFiles: Array.isArray(output.sourceFiles) ? output.sourceFiles : [],
    };

    if (finalOutput.sourceFiles.length > 0) {
        const retrievedFileNames = relevantDocuments.map(d => d.fileName);
        finalOutput.sourceFiles = finalOutput.sourceFiles.filter(fileName => retrievedFileNames.includes(fileName));
    }

    return finalOutput;
  }
);

import type {Metadata} from 'next';
import './globals.css';
import { Toaster } from "@/components/ui/toaster"
import { ThemeProvider } from '@/components/theme-provider';

export const metadata: Metadata = {
  title: 'VerseWise',
  description: 'Spiritual insight for seeking Christians',
};

export default function RootLayout({
  children,
}: Readonly<{
  children: React.ReactNode;
}>) {
  return (
    <html lang="en" suppressHydrationWarning>
      <head>
        <link rel="preconnect" href="https://fonts.googleapis.com" />
        <link rel="preconnect" href="https://fonts.gstatic.com" crossOrigin="anonymous" />
        <link href="https://fonts.googleapis.com/css2?family=Inter:wght@400;700&display=swap" rel="stylesheet" />
      </head>
      <body className="font-body antialiased">
        <ThemeProvider
          attribute="class"
          defaultTheme="system"
          enableSystem
          disableTransitionOnChange
        >
          {children}
          <Toaster />
        </ThemeProvider>
      </body>
    </html>
  );
}

"use client";

import * as React from "react";
import { BookOpen, Search, MessageSquare } from "lucide-react";
import { VerseWiseLogo } from "@/components/icons";

import {
  SidebarProvider,
  Sidebar,
  SidebarHeader,
  SidebarContent,
  SidebarMenu,
  SidebarMenuItem,
  SidebarMenuButton,
  SidebarFooter,
  SidebarInset,
  SidebarTrigger,
} from "@/components/ui/sidebar";
import {
  Tabs,
  TabsContent,
} from "@/components/ui/tabs";
import { Separator } from "@/components/ui/separator";
import { Avatar, AvatarFallback, AvatarImage } from "@/components/ui/avatar";
import { BibleViewer } from "@/components/bible-viewer";
import { BibleSearch, BibleSearchPassage } from "@/components/bible-search";
import { AiAssistant } from "@/components/ai-assistant";
import { useIsMobile } from "@/hooks/use-mobile";
import { ThemeToggle } from "@/components/theme-toggle";

export type BibleLocation = {
  book: string;
  chapter: number;
  verse: number;
};

export default function App() {
  const isMobile = useIsMobile();
  const [activeTab, setActiveTab] = React.useState("bible");
  const [bibleLocation, setBibleLocation] = React.useState<BibleLocation>({
    book: "Genesis",
    chapter: 1,
    verse: 1,
  });
  const [bibleVersion, setBibleVersion] = React.useState("esv");
  const [chatInput, setChatInput] = React.useState("");
  const [sidebarOpen, setSidebarOpen] = React.useState(true);

  const handleNavigateToVerse = (location: BibleLocation | BibleSearchPassage) => {
    const verse = Array.isArray(location.verses) ? location.verses[0] : location.verse ?? 1;
    setBibleLocation({
      book: location.book,
      chapter: location.chapter,
      verse: verse,
    });
    setActiveTab("bible");
  };

  const handleInterpretVerse = (verseRef: string, book: string, chapter: number, verse: number) => {
    setChatInput(`Tell me more about ${verseRef}`);
    setActiveTab("chat");
  };

  const handleVerseLinkClick = (verseRef: string) => {
    const parts = verseRef.match(/(\d*\s?[A-Za-z\s]+)\s(\d+):(\d+)/);
    if (parts) {
      handleNavigateToVerse({ book: parts[1].trim(), chapter: parseInt(parts[2]), verse: parseInt(parts[3]) });
    }
  };

  return (
    <SidebarProvider open={sidebarOpen} onOpenChange={setSidebarOpen} defaultOpen={true}>
      <Sidebar>
        <SidebarHeader>
          <div className="flex items-center gap-2">
            <VerseWiseLogo className="size-8 text-primary" />
            <span className="font-headline text-2xl font-bold text-primary">
              VerseWise
            </span>
          </div>
        </SidebarHeader>
        <SidebarContent>
          <SidebarMenu>
            <SidebarMenuItem>
              <SidebarMenuButton
                onClick={() => setActiveTab("bible")}
                isActive={activeTab === "bible"}
                tooltip={isMobile ? "" : "Bible"}
              >
                <BookOpen />
                <span className="font-body">Bible</span>
              </SidebarMenuButton>
            </SidebarMenuItem>
            <SidebarMenuItem>
              <SidebarMenuButton
                onClick={() => setActiveTab("search")}
                isActive={activeTab === "search"}
                tooltip={isMobile ? "" : "Search"}
              >
                <Search />
                <span className="font-body">Search</span>
              </SidebarMenuButton>
            </SidebarMenuItem>
            <SidebarMenuItem>
              <SidebarMenuButton
                onClick={() => setActiveTab("chat")}
                isActive={activeTab === "chat"}
                tooltip={isMobile ? "" : "AI Assistant"}
              >
                <MessageSquare />
                <span className="font-body">AI Assistant</span>
              </SidebarMenuButton>
            </SidebarMenuItem>
          </SidebarMenu>
        </SidebarContent>
        <SidebarFooter>
          <Separator />
          <div className="flex items-center justify-between p-2">
            <div className="flex items-center gap-2">
              <Avatar className="size-8">
                <AvatarImage src="https://placehold.co/40x40.png" data-ai-hint="user avatar" />
                <AvatarFallback>U</AvatarFallback>
              </Avatar>
              <span className="font-body text-sm font-medium">Guest User</span>
            </div>
            <ThemeToggle />
          </div>
        </SidebarFooter>
      </Sidebar>
      <SidebarInset className="flex flex-col">
        <header className="flex h-14 items-center gap-4 border-b bg-card px-4 sm:px-6">
          <SidebarTrigger />
          <h1 className="font-headline text-xl font-bold capitalize">{activeTab}</h1>
        </header>
        <main className="flex-1 overflow-auto">
          <Tabs value={activeTab} onValueChange={setActiveTab} className="h-full">
            <TabsContent value="bible" className="p-4 md:p-6 h-full mt-0">
              <BibleViewer
                location={bibleLocation}
                onVerseClick={handleInterpretVerse}
                setLocation={setBibleLocation}
                version={bibleVersion}
                setVersion={setBibleVersion}
              />
            </TabsContent>
            <TabsContent value="search" className="p-4 md:p-6 h-full mt-0">
              <BibleSearch onResultClick={handleNavigateToVerse} />
            </TabsContent>
            <TabsContent value="chat" className="p-4 md:p-6 h-full mt-0">
              <AiAssistant
                initialInput={chatInput}
                onVerseLinkClick={handleVerseLinkClick}
              />
            </TabsContent>
          </Tabs>
        </main>
      </SidebarInset>
    </SidebarProvider>
  );
}

"use client";

import * as React from "react";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select";
import { BIBLE_BOOKS, getChaptersForBook, getVersesForChapter, BIBLE_VERSIONS, getGreekVerses, getHebrewVerses, getDefinition, type DictionaryDefinition } from "@/lib/bible-data";
import type { BibleLocation } from "@/app/page";
import { ScrollArea } from "./ui/scroll-area";
import { Skeleton } from "./ui/skeleton";
import { LexiconModal } from "./lexicon-modal";

interface BibleViewerProps {
  location: BibleLocation;
  setLocation: (location: BibleLocation) => void;
  onVerseClick: (verseRef: string, book: string, chapter: number, verse: number) => void;
  version: string;
  setVersion: (version: string) => void;
}

export function BibleViewer({ location, setLocation, onVerseClick, version, setVersion }: BibleViewerProps) {
  const [chapters, setChapters] = React.useState<number[]>([]);
  const [verses, setVerses] = React.useState<string[]>([]);
  const [greekVerses, setGreekVerses] = React.useState<string[]>([]);
  const [hebrewVerses, setHebrewVerses] = React.useState<string[]>([]);
  const [isLoading, setIsLoading] = React.useState(true);
  const [isGreekLoading, setIsGreekLoading] = React.useState(false);
  const [isHebrewLoading, setIsHebrewLoading] = React.useState(false);
  const [isGreekOpen, setIsGreekOpen] = React.useState(false);

  // Lexicon Modal State
  const [selectedWord, setSelectedWord] = React.useState<string | null>(null);
  const [definitions, setDefinitions] = React.useState<DictionaryDefinition[] | null>(null);
  const [isLexiconLoading, setIsLexiconLoading] = React.useState(false);

  const verseRefs = React.useRef<Array<HTMLDivElement | null>>([]);

  React.useEffect(() => {
    const newChapters = getChaptersForBook(location.book);
    setChapters(newChapters);
    if (location.chapter > newChapters.length && newChapters.length > 0) {
      setLocation({ ...location, chapter: 1, verse: 1 });
    }
  }, [location.book, location.chapter, setLocation]);

  React.useEffect(() => {
    const fetchVerses = async () => {
      if (!location.book || !location.chapter) return;
      setIsLoading(true);
      const newVerses = await getVersesForChapter(location.book, location.chapter, version);
      setVerses(newVerses);
      if (location.verse > newVerses.length) {
        setLocation({ ...location, verse: 1 });
      }
      setIsLoading(false);
    };
    fetchVerses();
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [location.book, location.chapter, version]);

  // Fetch Greek verses when enabled or location changes
  React.useEffect(() => {
    const fetchGreek = async () => {
      if (!isGreekOpen || !location.book || !location.chapter) return;
      setIsGreekLoading(true);
      const newVerses = await getGreekVerses(location.book, location.chapter);
      setGreekVerses(newVerses);
      setIsGreekLoading(false);
    };
    fetchGreek();
  }, [isGreekOpen, location.book, location.chapter]);

  // Fetch Hebrew verses for OT
  React.useEffect(() => {
    const fetchHebrew = async () => {
      const bookIndex = BIBLE_BOOKS.indexOf(location.book);
      // 0-38 is OT
      if (!isGreekOpen || !location.book || !location.chapter || bookIndex >= 39) {
        setHebrewVerses([]);
        return;
      }
      setIsHebrewLoading(true);
      const newVerses = await getHebrewVerses(location.book, location.chapter);
      setHebrewVerses(newVerses);
      setIsHebrewLoading(false);
    };
    fetchHebrew();
  }, [isGreekOpen, location.book, location.chapter]);

  React.useEffect(() => {
    // Only scroll if not loading and the verse exists.
    if (!isLoading) {
      const verseElement = verseRefs.current[location.verse - 1];
      if (verseElement) {
        verseElement.scrollIntoView({ behavior: 'smooth', block: 'center' });
      }
    }
  }, [location.verse, isLoading, isGreekOpen]);

  // Handle word click for lexicon
  const handleWordClick = async (e: React.MouseEvent, word: string) => {
    e.stopPropagation(); // Prevent verse selection
    setSelectedWord(word);
    setIsLexiconLoading(true);
    const defs = await getDefinition(word);
    setDefinitions(defs);
    setIsLexiconLoading(false);
  };

  const closeLexicon = () => {
    setSelectedWord(null);
    setDefinitions(null);
  };

  const handleBookChange = (book: string) => {
    setLocation({ book, chapter: 1, verse: 1 });
  };

  const handleChapterChange = (chapter: string) => {
    setLocation({ ...location, chapter: parseInt(chapter), verse: 1 });
  };

  // Decide which Greek version name to display
  const bookIndex = BIBLE_BOOKS.indexOf(location.book);
  const isOT = bookIndex < 39;
  const greekLabel = isOT ? "Septuagint" : "Greek NT";

  // Decide grid layout
  const gridClass = isGreekOpen
    ? (isOT ? "grid grid-cols-3 gap-8" : "grid grid-cols-2 gap-8")
    : "";

  return (
    <div className="flex h-full flex-col gap-4">
      <LexiconModal
        word={selectedWord}
        definitions={definitions}
        isLoading={isLexiconLoading}
        isOpen={!!selectedWord}
        onClose={closeLexicon}
      />
      <div className="flex-none">
        <div className="flex gap-2 flex-wrap justify-center items-center">
          <Select value={version} onValueChange={setVersion}>
            <SelectTrigger className="w-auto sm:w-[180px] h-9">
              <SelectValue placeholder="Select a version" />
            </SelectTrigger>
            <SelectContent>
              {BIBLE_VERSIONS.map((v) => (
                <SelectItem key={v.id} value={v.id}>
                  {v.name}
                </SelectItem>
              ))}
            </SelectContent>
          </Select>
          <Select value={location.book} onValueChange={handleBookChange}>
            <SelectTrigger className="w-auto sm:w-[160px] h-9">
              <SelectValue placeholder="Select a book" />
            </SelectTrigger>
            <SelectContent>
              {BIBLE_BOOKS.map((book) => (
                <SelectItem key={book} value={book}>
                  {book}
                </SelectItem>
              ))}
            </SelectContent>
          </Select>
          <Select
            value={location.chapter.toString()}
            onValueChange={handleChapterChange}
          >
            <SelectTrigger className="w-auto sm:w-[90px] h-9">
              <SelectValue placeholder="Ch." />
            </SelectTrigger>
            <SelectContent>
              {chapters.map((chapter) => (
                <SelectItem key={chapter} value={chapter.toString()}>
                  {chapter}
                </SelectItem>
              ))}
            </SelectContent>
          </Select>
          <button
            onClick={() => setIsGreekOpen(!isGreekOpen)}
            className={`px-3 h-9 rounded-md border text-sm font-medium transition-colors hover:bg-accent hover:text-accent-foreground ${isGreekOpen ? "bg-primary text-primary-foreground underline" : "bg-transparent border-input"
              }`}
          >
            Greek/Hebrew
          </button>
        </div>
      </div>

      <Card className="flex-1 flex flex-col min-w-0">
        <CardHeader className={isGreekOpen ? `${gridClass} pb-2` : "pb-2"}>
          <CardTitle className="font-headline text-2xl font-bold">
            {location.book} {location.chapter}
          </CardTitle>
          {isGreekOpen && (
            <CardTitle className="font-headline text-2xl font-bold">
              {greekLabel}
            </CardTitle>
          )}
          {isGreekOpen && isOT && (
            <CardTitle className="font-headline text-2xl font-bold">
              Hebrew (WLC)
            </CardTitle>
          )}
        </CardHeader>
        <CardContent className="flex-1 overflow-hidden pt-0">
          <ScrollArea className="h-full pr-4">
            <div className="space-y-4 font-body text-lg leading-relaxed pt-4">
              {isLoading
                ? Array.from({ length: 15 }).map((_, i) => (
                  <div key={i} className="flex gap-2 items-start p-2">
                    <Skeleton className="h-4 w-6 mt-1" />
                    <div className="flex-1 space-y-2">
                      <Skeleton className="h-4 w-full" />
                      <Skeleton className="h-4 w-5/6" />
                    </div>
                  </div>
                ))
                : verses.map((text, index) => {
                  const verseNumber = index + 1;
                  const greekText = isGreekOpen && greekVerses[index] ? greekVerses[index] : "";
                  const hebrewText = isGreekOpen && isOT && hebrewVerses[index] ? hebrewVerses[index] : "";

                  return (
                    <div key={verseNumber} className={isGreekOpen ? `${gridClass} border-b border-border/40 py-4 first:pt-0 last:border-0` : ""}>
                      {/* English Verse */}
                      <div
                        ref={(el) => { verseRefs.current[index] = el; }}
                        className={`group p-2 rounded-md transition-colors duration-300 cursor-pointer ${!isGreekOpen ? "hover:bg-accent" : ""}`}
                        onClick={() => onVerseClick(`${location.book} ${location.chapter}:${verseNumber}`, location.book, location.chapter, verseNumber)}
                      >
                        <sup className="mr-2 font-semibold text-primary">{verseNumber}</sup>
                        <span className={!isGreekOpen ? "group-hover:text-primary" : ""}>
                          {text}
                        </span>
                      </div>

                      {/* Greek Verse */}
                      {isGreekOpen && (
                        <div
                          className="p-2 rounded-md transition-colors duration-300 cursor-pointer hover:bg-accent"
                          onClick={() => onVerseClick(`${location.book} ${location.chapter}:${verseNumber}`, location.book, location.chapter, verseNumber)}
                        >
                          {isGreekLoading ? (
                            <div className="space-y-2">
                              <Skeleton className="h-4 w-full" />
                              <Skeleton className="h-4 w-5/6" />
                            </div>
                          ) : (
                            <>
                              <sup className="mr-2 font-semibold text-primary">{verseNumber}</sup>
                              <span>
                                {greekText.split(' ').map((word, wIdx) => (
                                  <span
                                    key={wIdx}
                                    className="hover:text-primary hover:underline hover:decoration-primary cursor-pointer decoration-dotted underline-offset-4 decoration-2"
                                    onClick={(e) => handleWordClick(e, word)}
                                  >
                                    {word}{' '}
                                  </span>
                                ))}
                              </span>
                            </>
                          )}
                        </div>
                      )}

                      {/* Hebrew Verse */}
                      {isGreekOpen && isOT && (
                        <div
                          dir="rtl"
                          className="p-2 rounded-md transition-colors duration-300 cursor-pointer hover:bg-accent font-serif text-xl"
                          onClick={() => onVerseClick(`${location.book} ${location.chapter}:${verseNumber}`, location.book, location.chapter, verseNumber)}
                        >
                          {isHebrewLoading ? (
                            <div className="space-y-2">
                              <Skeleton className="h-4 w-full" />
                              <Skeleton className="h-4 w-5/6" />
                            </div>
                          ) : (
                            <>
                              <sup className="ml-2 font-semibold text-primary">{verseNumber}</sup>
                              <span>
                                {hebrewText.split(' ').map((word, wIdx) => (
                                  <span
                                    key={wIdx}
                                    className="hover:text-primary hover:underline hover:decoration-primary cursor-pointer decoration-dotted underline-offset-4 decoration-2"
                                    onClick={(e) => handleWordClick(e, word)}
                                  >
                                    {word}{' '}
                                  </span>
                                ))}
                              </span>
                            </>
                          )}
                        </div>
                      )}
                    </div>
                  );
                })}
            </div>
          </ScrollArea>
        </CardContent>
      </Card>
    </div>
  );
}

"use client"

import * as React from "react"
import { Slot } from "@radix-ui/react-slot"
import { VariantProps, cva } from "class-variance-authority"
import { PanelLeft } from "lucide-react"

import { useIsMobile } from "@/hooks/use-mobile"
import { cn } from "@/lib/utils"
import { Button } from "@/components/ui/button"
import { Input } from "@/components/ui/input"
import { Separator } from "@/components/ui/separator"
import { Sheet, SheetContent, SheetHeader, SheetTitle } from "@/components/ui/sheet"
import { Skeleton } from "@/components/ui/skeleton"
import {
  Tooltip,
  TooltipContent,
  TooltipProvider,
  TooltipTrigger,
} from "@/components/ui/tooltip"

const SIDEBAR_COOKIE_NAME = "sidebar_state"
const SIDEBAR_COOKIE_MAX_AGE = 60 * 60 * 24 * 7
const SIDEBAR_WIDTH = "16rem"
const SIDEBAR_WIDTH_MOBILE = "18rem"
const SIDEBAR_WIDTH_ICON = "3rem"
const SIDEBAR_KEYBOARD_SHORTCUT = "b"

type SidebarContext = {
  state: "expanded" | "collapsed"
  open: boolean
  setOpen: (open: boolean) => void
  openMobile: boolean
  setOpenMobile: (open: boolean) => void
  isMobile: boolean
  toggleSidebar: () => void
}

const SidebarContext = React.createContext<SidebarContext | null>(null)

function useSidebar() {
  const context = React.useContext(SidebarContext)
  if (!context) {
    throw new Error("useSidebar must be used within a SidebarProvider.")
  }

  return context
}

const SidebarProvider = React.forwardRef<
  HTMLDivElement,
  React.ComponentProps<"div"> & {
    defaultOpen?: boolean
    open?: boolean
    onOpenChange?: (open: boolean) => void
  }
>(
  (
    {
      defaultOpen = true,
      open: openProp,
      onOpenChange: setOpenProp,
      className,
      style,
      children,
      ...props
    },
    ref
  ) => {
    const isMobile = useIsMobile()
    const [openMobile, setOpenMobile] = React.useState(false)

    // This is the internal state of the sidebar.
    // We use openProp and setOpenProp for control from outside the component.
    const [_open, _setOpen] = React.useState(defaultOpen)
    const open = openProp ?? _open
    const setOpen = React.useCallback(
      (value: boolean | ((value: boolean) => boolean)) => {
        const openState = typeof value === "function" ? value(open) : value
        if (setOpenProp) {
          setOpenProp(openState)
        } else {
          _setOpen(openState)
        }

        // This sets the cookie to keep the sidebar state.
        document.cookie = `${SIDEBAR_COOKIE_NAME}=${openState}; path=/; max-age=${SIDEBAR_COOKIE_MAX_AGE}`
      },
      [setOpenProp, open]
    )

    // Helper to toggle the sidebar.
    const toggleSidebar = React.useCallback(() => {
      return isMobile
        ? setOpenMobile((open) => !open)
        : setOpen((open) => !open)
    }, [isMobile, setOpen, setOpenMobile])

    // Adds a keyboard shortcut to toggle the sidebar.
    React.useEffect(() => {
      const handleKeyDown = (event: KeyboardEvent) => {
        if (
          event.key === SIDEBAR_KEYBOARD_SHORTCUT &&
          (event.metaKey || event.ctrlKey)
        ) {
          event.preventDefault()
          toggleSidebar()
        }
      }

      window.addEventListener("keydown", handleKeyDown)
      return () => window.removeEventListener("keydown", handleKeyDown)
    }, [toggleSidebar])

    // We add a state so that we can do data-state="expanded" or "collapsed".
    // This makes it easier to style the sidebar with Tailwind classes.
    const state = open ? "expanded" : "collapsed"

    const contextValue = React.useMemo<SidebarContext>(
      () => ({
        state,
        open,
        setOpen,
        isMobile,
        openMobile,
        setOpenMobile,
        toggleSidebar,
      }),
      [state, open, setOpen, isMobile, openMobile, setOpenMobile, toggleSidebar]
    )

    return (
      <SidebarContext.Provider value={contextValue}>
        <TooltipProvider delayDuration={0}>
          <div
            style={
              {
                "--sidebar-width": SIDEBAR_WIDTH,
                "--sidebar-width-icon": SIDEBAR_WIDTH_ICON,
                ...style,
              } as React.CSSProperties
            }
            className={cn(
              "group/sidebar-wrapper flex min-h-svh w-full has-[[data-variant=inset]]:bg-sidebar",
              className
            )}
            ref={ref}
            {...props}
          >
            {children}
          </div>
        </TooltipProvider>
      </SidebarContext.Provider>
    )
  }
)
SidebarProvider.displayName = "SidebarProvider"

const Sidebar = React.forwardRef<
  HTMLDivElement,
  React.ComponentProps<"div"> & {
    side?: "left" | "right"
    variant?: "sidebar" | "floating" | "inset"
    collapsible?: "offcanvas" | "icon" | "none"
  }
>(
  (
    {
      side = "left",
      variant = "sidebar",
      collapsible = "offcanvas",
      className,
      children,
      ...props
    },
    ref
  ) => {
    const { isMobile, state, openMobile, setOpenMobile } = useSidebar()

    if (collapsible === "none") {
      return (
        <div
          className={cn(
            "flex h-full w-[--sidebar-width] flex-col bg-sidebar text-sidebar-foreground",
            className
          )}
          ref={ref}
          {...props}
        >
          {children}
        </div>
      )
    }

    if (isMobile) {
      return (
        <Sheet open={openMobile} onOpenChange={setOpenMobile} {...props}>
          <SheetContent
            data-sidebar="sidebar"
            data-mobile="true"
            className="w-[--sidebar-width] bg-sidebar p-0 text-sidebar-foreground [&>button]:hidden"
            style={
              {
                "--sidebar-width": SIDEBAR_WIDTH_MOBILE,
              } as React.CSSProperties
            }
            side={side}
          >
            <SheetHeader className="sr-only">
              <SheetTitle>Menu</SheetTitle>
            </SheetHeader>
            <div className="flex h-full w-full flex-col">{children}</div>
          </SheetContent>
        </Sheet>
      )
    }

    return (
      <div
        ref={ref}
        className="group peer hidden md:block text-sidebar-foreground"
        data-state={state}
        data-collapsible={state === "collapsed" ? collapsible : ""}
        data-variant={variant}
        data-side={side}
      >
        {/* This is what handles the sidebar gap on desktop */}
        <div
          className={cn(
            "duration-200 relative h-svh w-[--sidebar-width] bg-transparent transition-[width] ease-linear",
            "group-data-[collapsible=offcanvas]:w-0",
            "group-data-[side=right]:rotate-180",
            variant === "floating" || variant === "inset"
              ? "group-data-[collapsible=icon]:w-[calc(var(--sidebar-width-icon)_+_theme(spacing.4))]"
              : "group-data-[collapsible=icon]:w-[--sidebar-width-icon]"
          )}
        />
        <div
          className={cn(
            "duration-200 fixed inset-y-0 z-10 hidden h-svh w-[--sidebar-width] transition-[left,right,width] ease-linear md:flex",
            side === "left"
              ? "left-0 group-data-[collapsible=offcanvas]:left-[calc(var(--sidebar-width)*-1)]"
              : "right-0 group-data-[collapsible=offcanvas]:right-[calc(var(--sidebar-width)*-1)]",
            // Adjust the padding for floating and inset variants.
            variant === "floating" || variant === "inset"
              ? "p-2 group-data-[collapsible=icon]:w-[calc(var(--sidebar-width-icon)_+_theme(spacing.4)_+2px)]"
              : "group-data-[collapsible=icon]:w-[--sidebar-width-icon] group-data-[side=left]:border-r group-data-[side=right]:border-l",
            className
          )}
          {...props}
        >
          <div
            data-sidebar="sidebar"
            className="flex h-full w-full flex-col bg-sidebar group-data-[variant=floating]:rounded-lg group-data-[variant=floating]:border group-data-[variant=floating]:border-sidebar-border group-data-[variant=floating]:shadow"
          >
            {children}
          </div>
        </div>
      </div>
    )
  }
)
Sidebar.displayName = "Sidebar"

